// Array of quotes
const quotes = [
    { text: "Hard work beats talent when talent doesn't work hard.", author: "Tim Notke" },
    { text: "The only place where success comes before work is in the dictionary.", author: "Vidal Sassoon" },
    { text: "The harder I work, the luckier I get.", author: "Samuel Goldwyn" },
    { text: "Choose a job you love, and you will never have to work a day in your life.", author: "Confucius" },
    // Add more quotes as needed
];

// Function to change the banner content
function updateBanner() {
    // Get a random quote
    const randomQuote = quotes[Math.floor(Math.random() * quotes.length)];
    
    // Update the banner content
    document.getElementById('banner-quote').textContent = `"${randomQuote.text}"`;
    document.getElementById('banner-author').textContent = randomQuote.author;
}

// Initial banner update
updateBanner();

// Update the banner every 10 seconds (10000 milliseconds)
setInterval(updateBanner, 10000);
